              $.widget("ui.tooltip", $.ui.tooltip, {
              options: {
              content: function () {
                return $(this).prop('title');
              }
              }
              });
              $(function () {
              $('.item').attr('title', function(){
              return $(this).next('.tooltip').remove().html()
              })
              $(document).tooltip();
              });
